                        "MaxColors" = 8;                # Must be >= 2 and <= 12
                        "IconSize" = 32;
                        "Icons" = $(New-Object -TypeName "System.String[,]" -ArgumentList 13, 2);
                        "Colors" = @{"White" = 0;       # Change Number Order (0 - 11) to configure which colors are used 
                                     "Black" = 1;       # Color Names Must Stay the Same
                                     "Red" = 2;
                                     "Orange" = 3;
                                     "Yellow" = 4;
                                     "Green" = 5;
                                     "Blue" = 6;
                                     "Purple" = 7;
                                     "Brown" = 8;
                                     "Cyan" = 9;
                                     "Teal" = 10;
                                     "Gray" = 11;
                                     "Clear" = 12}}     # Clear Should Always be 12  

if (($Script:MyCodeSetup.MaxPegs -gt $Script:MyCodeSetup.MaxColors) -or ($Script:MyCodeSetup.MaxColors -gt 12))
{
  [System.Environment]::Exit(0)
}

