                       "Answer" = $(New-Object -TypeName "System.Int16[]" -ArgumentList $Script:MyCodeSetup.MaxPegs);
                       "Solved" = $False}

#region function Update-Turn
function Update-Turn() 
{
  <#
    .SYNOPSIS
    .DESCRIPTION
    .PARAMETER Value
    .INPUTS
    .OUTPUTS
    .EXAMPLE
    .NOTES
      Original Script By Kenneth D. Sweet
    .LINK
  #>
  [CmdletBinding()]
  param (
    [parameter(Mandatory=$True)]
    [Int]$Turn = $Script:MyCodeGame.Turn
  )
  Write-Verbose -Message "Start Update-Turn Function"
  Try
  {
    For ($Peg = 0; $Peg -le ($Script:MyCodeSetup.MaxPegs - 1); $Peg++)
    {
      $MyCode_Turn_PictureBox[$Turn, $Peg].Image = ([System.Drawing.Image]([System.Drawing.Image]::FromStream((New-Object -TypeName System.IO.MemoryStream(($$ = [System.Convert]::FromBase64String(($Script:MyCodeSetup.Icons[$Script:MyCodeSetup.Colors.Clear, 1]))), 0, $$.Length)))))
      $Script:MyCodeGame.Guess[$Turn, $Peg] = $Script:MyCodeSetup.Colors.Clear
      $MyCode_Turn_PictureBox[$Turn, $Peg].BackColor = [System.Drawing.Color]::White
    }
    For ($Count = 0; $Count -lt $Script:MyCodeSetup.MaxColors; $Count++)
    {
      $MyCode_Color_ToolStripButton[$Count].Visible = $True
    }
  }
  Catch
  {
    Write-Debug -Message "ErrMsg: $($Error[0].Exception.Message)"
    Write-Debug -Message "Line: $($Error[0].InvocationInfo.ScriptLineNumber)"
    Write-Debug -Message "Code: $(($Error[0].InvocationInfo.Line).Trim())"
  }
  Write-Verbose -Message "Finish Update-Turn Function"
}
#endregion

#region function Reset-Game
function Reset-Game() 
{
  <#
    .SYNOPSIS
    .DESCRIPTION
    .PARAMETER Value
    .INPUTS
    .OUTPUTS
    .EXAMPLE
    .NOTES
      Original Script By Kenneth D. Sweet
    .LINK
  #>
  [CmdletBinding()]
  param (
  )
  Write-Verbose -Message "Start Reset-Game Function"
  Try
  {
    For ($Peg = 0; $Peg -le ($Script:MyCodeSetup.MaxPegs - 1); $Peg++)
    {
      $Color = $Script:MyCodeSetup.Colors.Clear
      $Script:MyCodeGame.Answer[$Peg] = $Color
      While (@(0..($Script:MyCodeSetup.MaxPegs - 1) | ForEach-Object -Process { $Script:MyCodeGame.Answer[$_] }) -contains $Color)
      {
        $Color = Get-Random -Minimum 0 -Maximum $Script:MyCodeSetup.MaxColors
      }
      $Script:MyCodeGame.Answer[$Peg] = $Color
  
      $MyCode_Answer_PictureBox[$Peg].Image = ([System.Drawing.Image]([System.Drawing.Image]::FromStream((New-Object -TypeName System.IO.MemoryStream(($$ = [System.Convert]::FromBase64String(($Script:MyCodeSetup.Icons[$Script:MyCodeSetup.Colors.Clear, 1]))), 0, $$.Length)))))
      $MyCode_Answer_PictureBox[$Peg].BackColor = [System.Drawing.Color]::Black
      
      For ($Turn = ($Script:MyCodeSetup.MaxTurns - 1); $Turn -ge 0; $Turn--)
      {
        $MyCode_Turn_PictureBox[$Turn, $Peg].BackColor = [System.Drawing.Color]::Sienna
        $MyCode_Turn_PictureBox[$Turn, $Peg].Image = $Null

        $Script:MyCodeGame.Guess[$Turn, $Peg] = $Script:MyCodeSetup.Colors.Clear

        $MyCode_Clue_PictureBox[$Turn, $Peg].BackColor = [System.Drawing.Color]::Sienna
      }
    }
    $Script:MyCodeGame.Turn = 0
    $Script:MyCodeGame.Peg = 0
    $Script:MyCodeGame.Solved = $False

    Update-Turn -Turn $Script:MyCodeGame.Turn
  }
  Catch
  {
    Write-Debug -Message "ErrMsg: $($Error[0].Exception.Message)"
    Write-Debug -Message "Line: $($Error[0].InvocationInfo.ScriptLineNumber)"
    Write-Debug -Message "Code: $(($Error[0].InvocationInfo.Line).Trim())"
  }
  Write-Verbose -Message "Finish Reset-Game Function"
}
#endregion

#region function Check-Answer
function Check-Answer() 
{
  <#
    .SYNOPSIS
    .DESCRIPTION
    .PARAMETER Value
    .INPUTS
    .OUTPUTS
    .EXAMPLE
    .NOTES
      Original Script By Kenneth D. Sweet
    .LINK
  #>
  [CmdletBinding()]
  param (
    [parameter(Mandatory=$True)]
    [Int]$Turn = $Script:MyCodeGame.Turn
  )
  Write-Verbose -Message "Start Check-Answer Function"
  Try
  {
    $BlackPegs = -1
    $WhitePegs = -1
    $BlackFound = @()
    $ColorFound = @()
    For ($Peg = 0; $Peg -le ($Script:MyCodeSetup.MaxPegs - 1); $Peg++)
    {
      if ($Script:MyCodeGame.Answer[$Peg] -eq $Script:MyCodeGame.Guess[$Turn, $Peg])
      {
        $BlackPegs++
        $BlackFound += $Peg
        $ColorFound += $Script:MyCodeGame.Guess[$Turn, $Peg]
      }
    }
    For ($Peg = 0; $Peg -le ($Script:MyCodeSetup.MaxPegs - 1); $Peg++)
    {
      if (@(0..($Script:MyCodeSetup.MaxPegs - 1) | Where-Object -FilterScript { $BlackFound -notcontains $_ } | ForEach-Object -Process { $Script:MyCodeGame.Answer[$_] }) -contains $Script:MyCodeGame.Guess[$Turn, $Peg])
      {
        if ($ColorFound -notcontains $Script:MyCodeGame.Guess[$Turn, $Peg])
        {
          $WhitePegs++
        $ColorFound += $Script:MyCodeGame.Guess[$Turn, $Peg]
        }
      }
    }
    $CluePeg = 0
    for ($Count = 0; $Count -le $BlackPegs; $Count++)
    {
      $MyCode_Clue_PictureBox[$Turn, $CluePeg].BackColor = [System.Drawing.Color]::Black
      $CluePeg++
    }
    for ($Count = 0; $Count -le $WhitePegs; $Count++)
    {
      $MyCode_Clue_PictureBox[$Turn, $CluePeg].BackColor = [System.Drawing.Color]::White
      $CluePeg++
    }
    if ($BlackPegs -eq ($Script:MyCodeSetup.MaxPegs - 1))
    {
      For ($Peg = 0; $Peg -le ($Script:MyCodeSetup.MaxPegs - 1); $Peg++)
      {
        $MyCode_Answer_PictureBox[$Peg].Image = ([System.Drawing.Image]([System.Drawing.Image]::FromStream((New-Object -TypeName System.IO.MemoryStream(($$ = [System.Convert]::FromBase64String(($Script:MyCodeSetup.Icons[$Script:MyCodeGame.Answer[$Peg], 1]))), 0, $$.Length)))))
      }
      $Script:MyCodeGame.Solved = $True
    }
  }
  Catch
  {
    Write-Debug -Message "ErrMsg: $($Error[0].Exception.Message)"
    Write-Debug -Message "Line: $($Error[0].InvocationInfo.ScriptLineNumber)"
    Write-Debug -Message "Code: $(($Error[0].InvocationInfo.Line).Trim())"
  }
  Write-Verbose -Message "Finish Check-Answer Function"
}
