#region function KeyDown-MyCode_Form
function KeyDown-MyCode_Form()
{
  <#
    .SYNOPSIS
      KeyDown event for the MyCode_Form Control
    .DESCRIPTION
      KeyDown event for the MyCode_Form Control
    .PARAMETER Sender
       The Form Control that fired the Event
    .PARAMETER EventArg
       The Event Arguments for the Event
    .EXAMPLE
       KeyDown-MyCode_Form -Sender $This -EventArg $PSItem
    .INPUTS
    .OUTPUTS
    .NOTES
    .LINK
  #>
  [CmdletBinding()]
  param (
    [parameter(Mandatory = $True)]
    [Object]$Sender,
    [parameter(Mandatory = $True)]
    [Object]$EventArg
  )
  Write-Verbose -Message "Enter KeyDown Event for `$MyCode_Form"
  Try
  {
    if ($EventArg.Control -and $EventArg.Alt -and $EventArg.KeyCode -eq "F10")
    {
      if ($MyCode_Form.Tag)
      {
        $Script:VerbosePreference = "SilentlyContinue"
        $Script:DebugPreference = "SilentlyContinue"
        [Void][Window.Display]::Hide()
        $MyCode_Form.Tag = $False
      }
      else
      {
        $Script:VerbosePreference = "Continue"
        $Script:DebugPreference = "Continue"
        [Void][Window.Display]::Show()
        $MyCode_Form.Tag = $True
      }
      $MyCode_Form.Activate()
      $MyCode_Form.Select()
    }
  }
  Catch
  {
    Write-Debug -Message "ErrMsg: $($Error[0].Exception.Message)"
    Write-Debug -Message "Line: $($Error[0].InvocationInfo.ScriptLineNumber)"
    Write-Debug -Message "Code: $(($Error[0].InvocationInfo.Line).Trim())"
  }
  Write-Verbose -Message "Exit KeyDown Event for `$MyCode_Form"
}
#endregion
$MyCode_Form.add_KeyDown({KeyDown-MyCode_Form -Sender $This -EventArg $PSItem})

