#region function Click-MyCode_Color_ToolStripButton
function Click-MyCode_Color_ToolStripButton()
{
  <#
    .SYNOPSIS
      Click event for the MyCode_Color_ToolStripButton Control
    .DESCRIPTION
      Click event for the MyCode_Color_ToolStripButton Control
    .PARAMETER Sender
       The Form Control that fired the Event
    .PARAMETER EventArg
       The Event Arguments for the Event
    .EXAMPLE
       Click-MyCode_Color_ToolStripButton -Sender $This -EventArg $PSItem
    .INPUTS
    .OUTPUTS
    .NOTES
    .LINK
  #>
  [CmdletBinding()]
  param (
    [parameter(Mandatory = $True)]
    [Object]$Sender,
    [parameter(Mandatory = $True)]
    [Object]$EventArg
  )
  Write-Verbose -Message "Enter Click Event for `$MyCode_Color_ToolStripButton"
  Try
  {
    if ($Sender.Tag -eq -1)
    {
      Check-Answer -Turn $Script:MyCodeGame.Turn
      if ($Script:MyCodeGame.Solved)
      {
        $Result = [System.Windows.Forms.MessageBox]::Show("You have Solved the Code!", "Solved!", "OK", "Information")
        Reset-Game
      }
      else
      {
        $Script:MyCodeGame.Turn++
        if ($Script:MyCodeGame.Turn -eq $Script:MyCodeSetup.MaxTurns)
        {
          For ($Peg = 0; $Peg -le ($Script:MyCodeSetup.MaxPegs - 1); $Peg++)
          {
            $MyCode_Answer_PictureBox[$Peg].Image = ([System.Drawing.Image]([System.Drawing.Image]::FromStream((New-Object -TypeName System.IO.MemoryStream(($$ = [System.Convert]::FromBase64String(($Script:MyCodeSetup.Icons[$Script:MyCodeGame.Answer[$Peg], 1]))), 0, $$.Length)))))
          }
          $Result = [System.Windows.Forms.MessageBox]::Show("You have Failed to Solved the Code!", "Solved!", "OK", "Error")
          Reset-Game
        }
        else
        {
          Update-Turn -Turn $Script:MyCodeGame.Turn
        }
      }
    }
    else
    {
      if ($Script:MyCodeGame.Guess[$Script:MyCodeGame.Turn, $Script:MyCodeGame.Peg] -ne $Script:MyCodeSetup.Colors.Clear)
      {
        $MyCode_Color_ToolStripButton[$Script:MyCodeGame.Guess[$Script:MyCodeGame.Turn, $Script:MyCodeGame.Peg]].Visible = $True
      }
      if ($Sender.Tag -eq $Script:MyCodeSetup.Colors.Clear)
      {
        $MyCode_Turn_PictureBox[$Script:MyCodeGame.Turn, $Script:MyCodeGame.Peg].BackColor = [System.Drawing.Color]::White
      }
      else
      {
        $MyCode_Turn_PictureBox[$Script:MyCodeGame.Turn, $Script:MyCodeGame.Peg].BackColor = [System.Drawing.Color]::Black
        $MyCode_Color_ToolStripButton[$Sender.Tag].Visible = $False
      }
      $MyCode_Turn_PictureBox[$Script:MyCodeGame.Turn, $Script:MyCodeGame.Peg].Image = $Sender.Image
      $Script:MyCodeGame.Guess[$Script:MyCodeGame.Turn, $Script:MyCodeGame.Peg] = $Sender.Tag
    }
  }
  Catch
  {
    Write-Debug -Message "ErrMsg: $($Error[0].Exception.Message)"
    Write-Debug -Message "Line: $($Error[0].InvocationInfo.ScriptLineNumber)"
    Write-Debug -Message "Code: $(($Error[0].InvocationInfo.Line).Trim())"
  }
  Write-Verbose -Message "Exit Click Event for `$MyCode_Color_ToolStripButton"
}
